mapper.select:
select *
from apart a join trade t on (a.id = t.apart_id)



		a.dong like concat('%', #{dong},'%') and



		a.name like concat('%', #{aptName},'%') and


	a.regional_code = #{gugun} and t.deal_year = #{year} and t.deal_month = #{month}

mapper.update:
update trade
set deal_amount = #{dealAmount}
where id = #{id}
